import './App.css';
import React from "react";
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import 'bulma/css/bulma.min.css';
import Logo250 from './images/logo250x125.png';
import {faGithub} from "@fortawesome/free-brands-svg-icons";
import {faHome, faAddressCard, faUserCircle, faEnvelope, faTerminal} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

function App() {
    return (
        <Router>
            <div className="App">
                <Header/>
                <Switch>
                    <Route exact path={'/'}>
                        <text>Test</text>
                    </Route>
                    <Route exact path={'/about'}>
                        <text>About</text>
                    </Route>
                    <Route path={'*'} component={NotFound}/>
                </Switch>
                <Footer/>
            </div>
        </Router>
    );
}

class Header extends React.Component {
    state = {isLoaded: false};
    componentDidMount() {
        this.setState({isLoaded: true});
    }

    render() {
        function terminalToggle() {
            if(document.getElementById('logoText') != null) {
                let currentText = document.getElementById('logoText').innerText;
                if(currentText.charAt(0) !== '>') {
                    document.getElementById('logoText').innerText = '>' + currentText;
                }
                if (currentText.charAt(currentText.length - 1) === '_') {
                    currentText = currentText.substring(0, currentText.length - 1);
                } else {
                    currentText = currentText + '_';
                }
                document.getElementById('logoText').innerText = currentText;
            }
            if(document.getElementById('logoText').innerText.charAt(0) === '>') {
                setTimeout(terminalToggle, 1000);
            }
        }

        function toggleBurger() {
            document.querySelector('.navbar-menu').classList.toggle('is-active');
        }

        return (
            <section className="hero is-link is-medium">
                <model-viewer load={terminalToggle()}/>
                <div className="hero-head">
                    <nav className="navbar">
                        <div className="container">
                            <div className="navbar-brand">
                                <a className="navbar-item">
                                    <a className={'logo'} id={'logoText'}>Jack_</a>
                                </a>
                                <a role="button" className={`navbar-burger burger`} aria-label="menu"
                                   aria-expanded="false"
                                   data-target="navbarMenu" onClick={toggleBurger}
                                >
                                    <span aria-hidden={"true"}/>
                                    <span aria-hidden={"true"}/>
                                    <span aria-hidden={"true"}/>
                                </a>
                            </div>
                            <div id="navbarMenu" className={`navbar-menu`}>
                                <div className="navbar-end">
                                    <a className="navbar-item is-active" onClick={toggleBurger}>
                                        <FontAwesomeIcon icon={faHome} className={'icon'}/><a>Home</a>
                                    </a>
                                    <a className="navbar-item" href="https://Jack-Development.github.io/portfolio"
                                       onClick={toggleBurger}>
                                        <FontAwesomeIcon icon={faAddressCard} className={'icon'}/><a>Portfolio</a>
                                    </a>
                                    <a className="navbar-item" href="https://Jack-Development.github.io/about"
                                       onClick={toggleBurger}>
                                        <FontAwesomeIcon icon={faUserCircle} className={'icon'}/><a>About Me</a>
                                    </a>
                                    <a className="navbar-item" href="https://Jack-Development.github.io/contact"
                                       onClick={toggleBurger}>
                                        <FontAwesomeIcon icon={faEnvelope} className={'icon'}/><a>Contact</a>
                                    </a>
                                    <span className="navbar-item">
                            <a className="button is-link is-inverted is-rounded"
                               href="https://github.com/Jack-Development">
                                <span className="icon">
                                    <FontAwesomeIcon icon={faGithub}/>
                                </span>
                                <span>GitHub</span>
                            </a>
                        </span>
                                </div>
                            </div>
                        </div>
                    </nav>
                </div>
            </section>
        );
    }
}

class Footer extends React.Component {
    render() {
        return (
            <div className={'footerContainer'}>
                <footer id="footer">
                    <div><p>© 2021 Jack Shilton</p></div>
                </footer>
            </div>
        );
    }
}

const NotFound = () => (
    <div>
        <h1 className={'invalidText'}>404 - Not Found</h1>
    </div>
);

export default App;
